import React from 'react';
import Header from './Header';
import ItemsList from './ItemsList';
import Item from './Item';
import styled, { createGlobalStyle } from 'styled-components';
import { itunesApiRequest } from './utils';
import Palette from './palette';
import Widget1 from './stories/widget1';

const GlobalStyle = createGlobalStyle`
  html, body {
    height: 100%;
    width: 100%;
		margin: 0;
		background-color: ${Palette('Grey', 800)};
  }
`;
const Content = styled.div`
	width: 100%;
	height: 100%;
`;

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { searchResults: [], searchMovie: [] };
    this.updateSearch = this.updateSearch.bind(this);
    this.updateMovie = this.updateMovie.bind(this);
  }

  async updateSearch(text) {
    const response = await itunesApiRequest(text);
    this.setState({ searchResults: response.results });
  }
  async updateMovie(text) {
    const response = await itunesApiRequest(text);
    this.setState({ searchMovie: response.results });
  }

  render() {
    const { searchResults } = this.state;
    const { searchMovie } = this.state;

    return (
      <>
        <GlobalStyle />
        <Content>
          <Header startSearch={this.updateSearch} />
          <ItemsList items={searchResults} />
          <Item selectMovie={this.updateMovie} />
          <Widget1 items={searchMovie} />
        </Content>
      </>
    );
  }
}

export default App;
