import React from 'react';
import styled from 'styled-components';
import Palette from './palette';
import { itunesApiRequest } from './utils';

const Card = styled.button`
height: 400px;
  width: 150px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  text-align: center;
  white-space: nowrap;
	margin: 10px 5px;
	padding: 5px;
	background-color: ${Palette('Grey', 600)};
	color: ${Palette('White')};
	:hover {
		box-shadow: 0 0 0.75rem ${Palette('Secondary', 'dark')};
	}
`;

const CardText = styled.div`
	overflow: hidden;
	text-overflow: ellipsis;
`;

async function selectMovie(text) {
    const response = await itunesApiRequest(text);
    this.setState({ searchResults: response.results });
}


const Item = ({ kind, trackId, wrapperType, trackPrice, trackTimeMillis, artistName, trackName, artworkUrl100, releaseDate }) => (

    // <Card onClick={() => window.location.href = "http://www.google.com"}>
    <Card onClick={() => selectMovie(trackId)}>
        <div>{kind}</div>
        <div>{trackId}</div>
        <div>{wrapperType}</div>
        <div>{trackPrice}</div>
        <div>{releaseDate}</div>
        <div>{trackTimeMillis}</div>
        <CardText>{artistName}</CardText>
        <CardText>{trackName}</CardText>
        <div>
            <img src={artworkUrl100} />
        </div>
    </Card>

);


export default Item;
